.hamburger__checkbox {
  display: none; }

.hamburger__icon {
  background-color: #fff;
  height: 4.2rem;
  width: 4.2rem;
  position: fixed;
  top: 1.2rem;
  right: 2rem;
  border-radius: 50%;
