        text-decoration: underline; }

.btn-click:hover {
  transition: all 0.3s ease-in-out;
  transform: translateY(-0.5rem); }

.hamburger__checkbox {
  display: none; }

.hamburger__icon {
  background-color: #fff;
  height: 4.2rem;
  width: 4.2rem;
  position: fixed;
  top: 1.2rem;
  right: 2rem;
  border-radius: 50%;
  z-index: 2000;
  box-shadow: 0 1rem 5rem rgba(75, 60, 76, 0.3);
  text-align: center;
  cursor: pointer; }

.hamburger__background {
  height: 4rem;
  width: 4rem;
  border-radius: 50%;
  position: fixed;
  top: 1.4rem;
  right: 2rem;
  background-image: radial-gradient(#76a3a3 10%, #130f13);
  z-index: 1000;
  transition: transform 0.6s cubic-bezier(0.6, -0.28, 0.735, 0.045); }

.hamburger__nav {
  height: 100vh;
  position: fixed;
  top: 0;
  right: 0;
  z-index: 1500;
  opacity: 0;
  width: 0;
  transition: all 0.6s cubic-bezier(0.6, -0.28, 0.735, 0.045); }

.hamburger__content {
  width: 100%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-align: center; }
  .hamburger__content--menu {
    margin: 1rem; }
  .hamburger__content--link:link, .hamburger__content--link:visited {
    display: inline-block;
    font-size: 2rem;
    font-weight: 500;
    padding: 0.5rem 2rem;
    color: #fff;
    background-image: linear-gradient(120deg, transparent 0%, transparent 50%, #fff 50%);
    background-size: 250%;
    transition: all 0.4s ease-in-out; }
  .hamburger__content--link:hover, .hamburger__content--link:active {
    background-position: 100%;
    color: #5c8a8a;
    transform: translate(1rem); }

.hamburger__checkbox:checked ~ .hamburger__background {
  transform: scale(90); }

.hamburger__checkbox:checked ~ .hamburger__nav {
  width: 110%;
  opacity: 1; }

.hamburger__line {
  position: relative;
  margin-top: 2rem; }
  .hamburger__line, .hamburger__line::before, .hamburger__line::after {
    display: inline-block;
    width: 2.3rem;
    height: 2px;
    background-color: #4b3c4c; }
  .hamburger__line::before, .hamburger__line::after {
    content: "";
    position: absolute;
    left: 0;
    transition: all 0.2s; }
  .hamburger__line::before {
    top: -0.6rem; }
  .hamburger__line::after {
    top: 0.6rem; }

.hamburger__icon:hover .hamburger__line::before {
  top: -0.8rem; }

.hamburger__icon:hover .hamburger__line::after {
  top: 0.8rem; }

.hamburger__checkbox:checked + .hamburger__icon .hamburger__line {
  background-color: transparent; }

.hamburger__checkbox:checked + .hamburger__icon .hamburger__line::before {
  top: 0;
  transform: rotate(135deg); }

.hamburger__checkbox:checked + .hamburger__icon .hamburger__line::after {
  top: 0;
  transform: rotate(-135deg); }

